mapper.select:
SELECT 
    ID, 
    SUBJECT, 
    BODY, 
    POST_DATE,
    CATEGORY_ID, 
    SUB_CATEGORY_ID, 
    CREATED_BY, 
    CREATED_DATE, 
    LAST_MODIFIED_BY, 
    LAST_MODIFIED_DATE, 
    VERSION 
FROM ENTRY E 


        SUBJECT like #{subject}


        AND CATEGORY_ID like #{categoryId}


        AND POST_DATE >= #{fromPostDate}


        AND POST_DATE <= #{toPostDate}


LIMIT #{pageSize}
OFFSET #{offset}

SELECT 
    COUNT(*) 
FROM ENTRY E 


        SUBJECT LIKE #{subject}


        AND CATEGORY_ID LIKE #{categoryId}


        AND POST_DATE >= #{fromPostDate}


        AND POST_DATE <= #{toPostDate}
